public class Foo {
	public void foo() {
		for(;;) {
			Account account = new Account();
			insert account;
		}
	}
}

public class Foo {
	public void foo() {
		while(true) {
			Account account = new Account();
			insert account;
		}
	}
}

public class Foo {
	public void foo() {
		do {
			Account account = new Account();
			insert account;
		} while(true);
	}
}

public class Foo {
	public void foo() {
		List<Account> accounts = new List<Account>();

		while(true) {
			Account account = new Account();
			accounts.add(account);
		}

		insert accounts;
	}
}
