test-data.code-fragment:
public class Foo {
  public static void bar() {
    boolean a, b = true;
    try { // 2 * 2 + 2
      if (true) { // 2
        List buz = new ArrayList();
      }

      for(int i = 0; i < 19; i++) { // 2
        List buz = new ArrayList();
      }
    } catch(Exception e) {
      if (true) { // 2
        e.printStackTrace();
      }
    }

    int j = 0;
    if (true || a && b) { // 4
      j = 10;
      return;
    }

    while (j++ < 20) { // 2
      List buz = new ArrayList();
    }

    switch(j) { // 7
      case 1:
      case 2: break;
      case 3: j = 5; break;
      case 4: if (b && a) { bar(); } break;
      default: break;
    }

    do { // 3
        List buz = new ArrayList();
    } while (a && j++ < 30);

    if (b) { // 2
      return;
    }
  }
}

test-code.code:
public class Foo {
  public static void bar() {
    boolean a, b = true;
    int j = 0;

    switch (j) { // 5
      case 0:
      case 1:
      case 3: if (a || b) {} break;
    }

    switch (j) { // * 5
      case 0:
      case 1:
      case 3: if (a || b) {} break;
    }

    if (true || a && b); // * 4
    while (j++ < 20);    // * 2
  }
}

class Foo {
  void bar() {
    boolean a, b;
    try {
      switch(j) { // 7
      case 1:
      case 2: break;
      case 3: j = 5; break;
      case 4: if (b && a) { bar(); } break;
      default: break;
      }

      switch(j) { // * 7
      case 1:
      case 2: break;
      case 3: j = 5; break;
      case 4: if (b && a) { bar(); } break;
      default: break;
      }

      if (true || a || b); // * 4

    } catch (ScaryException e) {
      if (true || a); // + 3
    }
  }
}

