import junit.framework.TestCase;
public class Foo extends TestCase {
   void testBar() { 
    boolean bar; 
    assertFalse(!bar);
   } 
}

import junit.framework.TestCase;
public class Foo extends TestCase {
   void testBar() { 
    boolean bar; 
    assertTrue(!bar);
   } 
}

import junit.framework.TestCase;
public class Foo extends TestCase {
   void testBar() { 
    boolean bar; 
    assertTrue(bar);
    assertFalse(bar);
   } 
}

public class Foo {
   void testBar() { 
    boolean bar; 
    assertFalse(!bar);
   } 
}

import org.junit.Test;
public class Foo {
   @Test
   void testBar() { 
    boolean bar; 
    assertFalse(!bar);
   } 
}

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertFalse;
public class Foo {
   @Test
   void testBar() { 
    boolean bar; 
    assertFalse(!bar);
   } 
}
