public class Foo {
 public void test1() {
   for(;;) {
       String a = new String();
   }
 }
}

public class Foo {
 public void test1() {
   while(true) {
       String a = new String();
   }
 }
}

public class Foo {
 public void test1() {
   do{
       String a = new String();
   }while(true) ;
 }
}

public class Foo {
 public void test1() {
   do{
       String a = new String();
       String b = new String();
   }while(true) ;
 }
}

public class Foo {
 public void test1() {
   for(;;) {
       throw new Exception();
   }
 }
}

public class Foo {
 public String test1() {
   for(;;) {
       return new String();
   }
 }
}

import java.util.ArrayList;
import java.io.File;

public class TestInstantiationInLoop {
    public static void main(String args[]) {
        for (String test : new ArrayList<String>()) { // facetious but simple example
            System.out.println(test);
        }
        for (String filename : new File("subdirectory").list()) { // complex but realistically conceivable (albeit exagerrated/oversimplified/rarely advisable) example
            System.out.println(filename);
        }
    }
}
