public class DumbSwitch {
	public void foo(int i) {
		switch (i) {
			case 0:
			{
				System.err.println("I am a fish.");
			}
		}
	}
}

public class DumbSwitch {
	public void foo(int i) {
		switch (i) {
			case 0:
			{
				System.err.println("I am a fish.");
			}
			case 1:
			{
				System.exit();
			}
		}
	}
}

public class ValidSwitch {
	public void foo(int i) {
		switch (i) {
			case 0:
			{
				System.err.println("I am a fish.");
			}
			case 1:
			{
				System.exit();
			}
			case 2:
			{
				// ...
			}
		}
	}
}
